
public class ComplexNumbersTest {
	 public static void main(String[] args) {
	        // TODO Auto-generated method stub
	        Complex c1 = new Complex(4.0, 8.5);
	        Complex c2 = new Complex(8.0, 4.5);


	        System.out.println("Adding '4+8.5i' to '8+4.5i");
	        System.out.println(c1.complexAdd(c2));
	        System.out.println("Subtracting '4+8.5i' from '8+4.5i");
	        System.out.println(c1.complexSubtract(c2));
	        
	        


	    }

}
